import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  Box,
  Card,
  CardContent,
  Button,
  Grid,
  Chip,
  IconButton,
  Menu,
  MenuItem,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Alert,
  CircularProgress,
} from '@mui/material';
import {
  Add as AddIcon,
  MoreVert as MoreVertIcon,
  Edit as EditIcon,
  Delete as DeleteIcon,
  Publish as PublishIcon,
  Visibility as ViewIcon,
} from '@mui/icons-material';
import { useAuth } from '../contexts/AuthContext';
import { api } from '../services/api.ts';

const Dashboard = () => {
  const [models, setModels] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [anchorEl, setAnchorEl] = useState(null);
  const [selectedModel, setSelectedModel] = useState(null);
  const [publishDialogOpen, setPublishDialogOpen] = useState(false);
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const { user } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    fetchModels();
  }, []);

  const fetchModels = async () => {
    try {
      setLoading(true);
      const response = await api.get('/models');
      setModels(response.data.models);
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to fetch models');
    } finally {
      setLoading(false);
    }
  };

  const handleMenuClick = (event, model) => {
    setAnchorEl(event.currentTarget);
    setSelectedModel(model);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
    setSelectedModel(null);
  };

  const handleEdit = () => {
    if (selectedModel) {
      navigate(`/models/${selectedModel.id}/edit`);
    }
    handleMenuClose();
  };

  const handleViewData = () => {
    if (selectedModel && selectedModel.isPublished) {
      navigate(`/data/${selectedModel.name}`);
    }
    handleMenuClose();
  };

  const handlePublish = () => {
    setPublishDialogOpen(true);
    handleMenuClose();
  };

  const confirmPublish = async () => {
    if (!selectedModel) return;

    try {
      await api.post(`/models/${selectedModel.id}/publish`);
      setPublishDialogOpen(false);
      fetchModels();
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to publish model');
    }
  };

  const handleDelete = () => {
    setDeleteDialogOpen(true);
    handleMenuClose();
  };

  const confirmDelete = async () => {
    if (!selectedModel) return;

    try {
      await api.delete(`/models/${selectedModel.id}`);
      setDeleteDialogOpen(false);
      fetchModels();
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to delete model');
    }
  };

  const canEdit = (model) => {
    return model.createdBy === user?.id || user?.role === 'Admin';
  };

  const canPublish = (model) => {
    return !model.isPublished && (user?.role === 'Admin' || user?.role === 'Manager');
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="50vh">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
        <Typography variant="h4" component="h1">
          Model Dashboard
        </Typography>
        {(user?.role === 'Admin' || user?.role === 'Manager') && (
          <Button
            variant="contained"
            startIcon={<AddIcon />}
            onClick={() => navigate('/models/new')}
          >
            Create Model
          </Button>
        )}
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }} onClose={() => setError('')}>
          {error}
        </Alert>
      )}

      <Grid container spacing={3}>
        {models.map((model) => (
          <Grid item xs={12} sm={6} md={4} key={model.id}>
            <Card>
              <CardContent>
                <Box display="flex" justifyContent="space-between" alignItems="flex-start" mb={2}>
                  <Typography variant="h6" component="h2">
                    {model.name}
                  </Typography>
                  <IconButton
                    size="small"
                    onClick={(e) => handleMenuClick(e, model)}
                    disabled={!canEdit(model) && !canPublish(model) && !model.isPublished}
                  >
                    <MoreVertIcon />
                  </IconButton>
                </Box>

                <Typography variant="body2" color="text.secondary" gutterBottom>
                  Table: {model.tableName}
                </Typography>

                <Typography variant="body2" color="text.secondary" gutterBottom>
                  Fields: {model.definition.fields?.length || 0}
                </Typography>

                <Box display="flex" gap={1} mt={2}>
                  <Chip
                    label={model.isPublished ? 'Published' : 'Draft'}
                    color={model.isPublished ? 'success' : 'default'}
                    size="small"
                  />
                  {model.definition.ownerField && (
                    <Chip label="Ownership" color="info" size="small" />
                  )}
                </Box>

                <Typography variant="caption" display="block" sx={{ mt: 1 }}>
                  Created: {new Date(model.createdAt).toLocaleDateString()}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      {models.length === 0 && (
        <Box textAlign="center" py={4}>
          <Typography variant="h6" color="text.secondary">
            No models found
          </Typography>
          <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
            Create your first model to get started
          </Typography>
        </Box>
      )}

      {/* Context Menu */}
      <Menu
        anchorEl={anchorEl}
        open={Boolean(anchorEl)}
        onClose={handleMenuClose}
      >
        {selectedModel?.isPublished && (
          <MenuItem onClick={handleViewData}>
            <ViewIcon sx={{ mr: 1 }} />
            View Data
          </MenuItem>
        )}
        {selectedModel && canEdit(selectedModel) && (
          <MenuItem onClick={handleEdit}>
            <EditIcon sx={{ mr: 1 }} />
            Edit
          </MenuItem>
        )}
        {selectedModel && canPublish(selectedModel) && (
          <MenuItem onClick={handlePublish}>
            <PublishIcon sx={{ mr: 1 }} />
            Publish
          </MenuItem>
        )}
        {selectedModel && canEdit(selectedModel) && (
          <MenuItem onClick={handleDelete} sx={{ color: 'error.main' }}>
            <DeleteIcon sx={{ mr: 1 }} />
            Delete
          </MenuItem>
        )}
      </Menu>

      {/* Publish Confirmation Dialog */}
      <Dialog open={publishDialogOpen} onClose={() => setPublishDialogOpen(false)}>
        <DialogTitle>Publish Model</DialogTitle>
        <DialogContent>
          <Typography>
            Are you sure you want to publish "{selectedModel?.name}"? This will create the database table
            and make the model available for data management.
          </Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setPublishDialogOpen(false)}>Cancel</Button>
          <Button onClick={confirmPublish} variant="contained">
            Publish
          </Button>
        </DialogActions>
      </Dialog>

      {/* Delete Confirmation Dialog */}
      <Dialog open={deleteDialogOpen} onClose={() => setDeleteDialogOpen(false)}>
        <DialogTitle>Delete Model</DialogTitle>
        <DialogContent>
          <Typography>
            Are you sure you want to delete "{selectedModel?.name}"? This action cannot be undone.
            {selectedModel?.isPublished && ' The database table will also be dropped.'}
          </Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDeleteDialogOpen(false)}>Cancel</Button>
          <Button onClick={confirmDelete} variant="contained" color="error">
            Delete
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default Dashboard;
