import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  Box,
  Button,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Checkbox,
  FormControlLabel,
  Alert,
  CircularProgress,
  Chip,
  Pagination,
  Grid,
} from '@mui/material';
import {
  Add as AddIcon,
  Edit as EditIcon,
  Delete as DeleteIcon,
  ArrowBack as ArrowBackIcon,
  Search as SearchIcon,
} from '@mui/icons-material';
import { useAuth } from '../contexts/AuthContext';
import { api } from '../services/api';

const DataManager = () => {
  const { modelName } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();

  const [loading, setLoading] = useState(true);
  const [records, setRecords] = useState([]);
  const [modelDefinition, setModelDefinition] = useState(null);
  const [pagination, setPagination] = useState({
    page: 1,
    limit: 10,
    total: 0,
    pages: 0,
  });
  const [search, setSearch] = useState('');
  const [sortBy, setSortBy] = useState('');
  const [sortOrder, setSortOrder] = useState('ASC');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Dialog states
  const [dialogOpen, setDialogOpen] = useState(false);
  const [editingRecord, setEditingRecord] = useState(null);
  const [formData, setFormData] = useState({});
  const [saving, setSaving] = useState(false);

  useEffect(() => {
    fetchModelDefinition();
  }, [modelName]);

  useEffect(() => {
    if (modelDefinition) {
      fetchRecords();
    }
  }, [modelDefinition, pagination.page, search, sortBy, sortOrder]);

  const fetchModelDefinition = async () => {
    try {
      const response = await api.get('/models');
      const model = response.data.models.find(m => m.name === modelName && m.isPublished);
      if (!model) {
        setError('Model not found or not published');
        return;
      }
      setModelDefinition(model);
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to fetch model definition');
    }
  };

  const fetchRecords = async () => {
    try {
      setLoading(true);
      const params = {
        page: pagination.page,
        limit: pagination.limit,
        ...(search && { search }),
        ...(sortBy && { sortBy, sortOrder }),
      };

      const response = await api.get(`/${modelName}`, { params });
      setRecords(response.data.records);
      setPagination(response.data.pagination);
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to fetch records');
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = (e) => {
    setSearch(e.target.value);
    setPagination(prev => ({ ...prev, page: 1 }));
  };

  const handleSort = (field) => {
    if (sortBy === field) {
      setSortOrder(sortOrder === 'ASC' ? 'DESC' : 'ASC');
    } else {
      setSortBy(field);
      setSortOrder('ASC');
    }
  };

  const handlePageChange = (event, page) => {
    setPagination(prev => ({ ...prev, page }));
  };

  const handleAdd = () => {
    setEditingRecord(null);
    setFormData({});
    setDialogOpen(true);
  };

  const handleEdit = (record) => {
    setEditingRecord(record);
    setFormData({ ...record });
    setDialogOpen(true);
  };

  const handleDelete = async (recordId) => {
    if (!window.confirm('Are you sure you want to delete this record?')) {
      return;
    }

    try {
      await api.delete(`/${modelName}/${recordId}`);
      setSuccess('Record deleted successfully');
      fetchRecords();
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to delete record');
    }
  };

  const handleSave = async () => {
    try {
      setSaving(true);
      if (editingRecord) {
        await api.put(`/${modelName}/${editingRecord.id}`, formData);
        setSuccess('Record updated successfully');
      } else {
        await api.post(`/${modelName}`, formData);
        setSuccess('Record created successfully');
      }
      setDialogOpen(false);
      fetchRecords();
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to save record');
    } finally {
      setSaving(false);
    }
  };

  const handleInputChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value,
    }));
  };

  const canCreate = () => {
    if (!modelDefinition) return false;
    const permissions = modelDefinition.definition.rbac[user?.role] || [];
    return permissions.includes('all') || permissions.includes('create');
  };

  const canUpdate = () => {
    if (!modelDefinition) return false;
    const permissions = modelDefinition.definition.rbac[user?.role] || [];
    return permissions.includes('all') || permissions.includes('update');
  };

  const canDelete = () => {
    if (!modelDefinition) return false;
    const permissions = modelDefinition.definition.rbac[user?.role] || [];
    return permissions.includes('all') || permissions.includes('delete');
  };

  if (loading && !modelDefinition) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="50vh">
        <CircularProgress />
      </Box>
    );
  }

  if (!modelDefinition) {
    return (
      <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
        <Alert severity="error">
          Model not found or not published
        </Alert>
      </Container>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Box display="flex" alignItems="center" mb={3}>
        <IconButton onClick={() => navigate('/models')} sx={{ mr: 2 }}>
          <ArrowBackIcon />
        </IconButton>
        <Typography variant="h4" component="h1">
          {modelName} Data
        </Typography>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }} onClose={() => setError('')}>
          {error}
        </Alert>
      )}

      {success && (
        <Alert severity="success" sx={{ mb: 2 }} onClose={() => setSuccess('')}>
          {success}
        </Alert>
      )}

      <Paper elevation={3} sx={{ p: 3 }}>
        {/* Search and Actions */}
        <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
          <TextField
            placeholder="Search records..."
            value={search}
            onChange={handleSearch}
            InputProps={{
              startAdornment: <SearchIcon sx={{ mr: 1, color: 'text.secondary' }} />,
            }}
            sx={{ width: 300 }}
          />
          {canCreate() && (
            <Button
              variant="contained"
              startIcon={<AddIcon />}
              onClick={handleAdd}
            >
              Add Record
            </Button>
          )}
        </Box>

        {/* Records Table */}
        <TableContainer>
          <Table>
            <TableHead>
              <TableRow>
                {modelDefinition.definition.fields.map((field) => (
                  <TableCell
                    key={field.name}
                    onClick={() => handleSort(field.name)}
                    sx={{ cursor: 'pointer', userSelect: 'none' }}
                  >
                    {field.name}
                    {sortBy === field.name && (
                      <Chip
                        label={sortOrder}
                        size="small"
                        sx={{ ml: 1 }}
                      />
                    )}
                  </TableCell>
                ))}
                <TableCell>Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {records.map((record) => (
                <TableRow key={record.id}>
                  {modelDefinition.definition.fields.map((field) => (
                    <TableCell key={field.name}>
                      {field.type === 'boolean' ? (
                        <Checkbox checked={record[field.name]} disabled />
                      ) : (
                        String(record[field.name] || '')
                      )}
                    </TableCell>
                  ))}
                  <TableCell>
                    <Box display="flex" gap={1}>
                      {canUpdate() && (
                        <IconButton
                          size="small"
                          onClick={() => handleEdit(record)}
                        >
                          <EditIcon />
                        </IconButton>
                      )}
                      {canDelete() && (
                        <IconButton
                          size="small"
                          onClick={() => handleDelete(record.id)}
                          color="error"
                        >
                          <DeleteIcon />
                        </IconButton>
                      )}
                    </Box>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>

        {records.length === 0 && !loading && (
          <Box textAlign="center" py={4}>
            <Typography color="text.secondary">
              No records found
            </Typography>
          </Box>
        )}

        {/* Pagination */}
        {pagination.pages > 1 && (
          <Box display="flex" justifyContent="center" mt={3}>
            <Pagination
              count={pagination.pages}
              page={pagination.page}
              onChange={handlePageChange}
              color="primary"
            />
          </Box>
        )}
      </Paper>

      {/* Add/Edit Dialog */}
      <Dialog open={dialogOpen} onClose={() => setDialogOpen(false)} maxWidth="md" fullWidth>
        <DialogTitle>
          {editingRecord ? 'Edit Record' : 'Add New Record'}
        </DialogTitle>
        <DialogContent>
          <Grid container spacing={2} sx={{ mt: 1 }}>
            {modelDefinition.definition.fields.map((field) => (
              <Grid item xs={12} sm={6} key={field.name}>
                {field.type === 'boolean' ? (
                  <FormControlLabel
                    control={
                      <Checkbox
                        checked={formData[field.name] || false}
                        onChange={(e) => handleInputChange(field.name, e.target.checked)}
                      />
                    }
                    label={field.name}
                  />
                ) : field.type === 'date' ? (
                  <TextField
                    fullWidth
                    label={field.name}
                    type="datetime-local"
                    value={formData[field.name] || ''}
                    onChange={(e) => handleInputChange(field.name, e.target.value)}
                    required={field.required}
                    InputLabelProps={{ shrink: true }}
                  />
                ) : (
                  <TextField
                    fullWidth
                    label={field.name}
                    type={field.type === 'number' ? 'number' : 'text'}
                    value={formData[field.name] || ''}
                    onChange={(e) => handleInputChange(field.name, e.target.value)}
                    required={field.required}
                    multiline={field.type === 'text'}
                    rows={field.type === 'text' ? 3 : 1}
                  />
                )}
              </Grid>
            ))}
          </Grid>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDialogOpen(false)} disabled={saving}>
            Cancel
          </Button>
          <Button onClick={handleSave} variant="contained" disabled={saving}>
            {saving ? 'Saving...' : editingRecord ? 'Update' : 'Create'}
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default DataManager;
