import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import {
  Container,
  Paper,
  TextField,
  Button,
  Typography,
  Box,
  Grid,
  Card,
  CardContent,
  IconButton,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  FormControlLabel,
  Checkbox,
  Chip,
  Alert,
  CircularProgress,
  Divider,
} from '@mui/material';
import {
  Add as AddIcon,
  Delete as DeleteIcon,
  Save as SaveIcon,
  ArrowBack as ArrowBackIcon,
} from '@mui/icons-material';
import { useAuth } from '../contexts/AuthContext';
import { api } from '../services/api.ts';

const ModelEditor = () => {
  const { id } = useParams();
  const isEdit = Boolean(id);
  const navigate = useNavigate();
  const { user } = useAuth();

  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const [modelData, setModelData] = useState({
    name: '',
    tableName: '',
    definition: {
      fields: [],
      ownerField: '',
      rbac: {
        Admin: ['all'],
        Manager: ['create', 'read', 'update'],
        Viewer: ['read'],
      },
    },
  });

  useEffect(() => {
    if (isEdit) {
      fetchModel();
    }
  }, [id, isEdit]);

  const fetchModel = async () => {
    try {
      setLoading(true);
      const response = await api.get(`/models/${id}`);
      const model = response.data.model;
      setModelData({
        name: model.name,
        tableName: model.tableName,
        definition: model.definition,
      });
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to fetch model');
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (field, value) => {
    if (field.includes('.')) {
      const [parent, child] = field.split('.');
      setModelData(prev => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value,
        },
      }));
    } else {
      setModelData(prev => ({
        ...prev,
        [field]: value,
      }));
    }
  };

  const addField = () => {
    setModelData(prev => ({
      ...prev,
      definition: {
        ...prev.definition,
        fields: [
          ...prev.definition.fields,
          {
            name: '',
            type: 'string',
            required: false,
            default: '',
            unique: false,
          },
        ],
      },
    }));
  };

  const updateField = (index, field, value) => {
    setModelData(prev => ({
      ...prev,
      definition: {
        ...prev.definition,
        fields: prev.definition.fields.map((f, i) =>
          i === index ? { ...f, [field]: value } : f
        ),
      },
    }));
  };

  const removeField = (index) => {
    setModelData(prev => ({
      ...prev,
      definition: {
        ...prev.definition,
        fields: prev.definition.fields.filter((_, i) => i !== index),
      },
    }));
  };

  const updateRbac = (role, permissions) => {
    setModelData(prev => ({
      ...prev,
      definition: {
        ...prev.definition,
        rbac: {
          ...prev.definition.rbac,
          [role]: permissions,
        },
      },
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSaving(true);
    setError('');
    setSuccess('');

    try {
      // Generate table name if not provided
      const finalTableName = modelData.tableName || `${modelData.name.toLowerCase()}s`;

      const payload = {
        name: modelData.name,
        tableName: finalTableName,
        definition: modelData.definition,
      };

      if (isEdit) {
        await api.put(`/models/${id}`, payload);
        setSuccess('Model updated successfully');
      } else {
        await api.post('/models', payload);
        setSuccess('Model created successfully');
        navigate('/models');
      }
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to save model');
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="50vh">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Box display="flex" alignItems="center" mb={3}>
        <IconButton onClick={() => navigate('/models')} sx={{ mr: 2 }}>
          <ArrowBackIcon />
        </IconButton>
        <Typography variant="h4" component="h1">
          {isEdit ? 'Edit Model' : 'Create New Model'}
        </Typography>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }} onClose={() => setError('')}>
          {error}
        </Alert>
      )}

      {success && (
        <Alert severity="success" sx={{ mb: 2 }} onClose={() => setSuccess('')}>
          {success}
        </Alert>
      )}

      <Paper elevation={3} sx={{ p: 3 }}>
        <Box component="form" onSubmit={handleSubmit}>
          <Grid container spacing={3}>
            {/* Basic Information */}
            <Grid item xs={12}>
              <Typography variant="h6" gutterBottom>
                Basic Information
              </Typography>
              <Grid container spacing={2}>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label="Model Name"
                    value={modelData.name}
                    onChange={(e) => handleInputChange('name', e.target.value)}
                    required
                    disabled={isEdit && modelData.definition?.isPublished}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    fullWidth
                    label="Table Name"
                    value={modelData.tableName}
                    onChange={(e) => handleInputChange('tableName', e.target.value)}
                    placeholder="Auto-generated if empty"
                    disabled={isEdit && modelData.definition?.isPublished}
                  />
                </Grid>
              </Grid>
            </Grid>

            <Divider sx={{ my: 2 }} />

            {/* Fields */}
            <Grid item xs={12}>
              <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
                <Typography variant="h6">
                  Fields ({modelData.definition.fields.length})
                </Typography>
                <Button
                  variant="outlined"
                  startIcon={<AddIcon />}
                  onClick={addField}
                  disabled={isEdit && modelData.definition?.isPublished}
                >
                  Add Field
                </Button>
              </Box>

              {modelData.definition.fields.map((field, index) => (
                <Card key={index} sx={{ mb: 2 }}>
                  <CardContent>
                    <Grid container spacing={2} alignItems="center">
                      <Grid item xs={12} sm={3}>
                        <TextField
                          fullWidth
                          label="Field Name"
                          value={field.name}
                          onChange={(e) => updateField(index, 'name', e.target.value)}
                          required
                          disabled={isEdit && modelData.definition?.isPublished}
                        />
                      </Grid>
                      <Grid item xs={12} sm={2}>
                        <FormControl fullWidth>
                          <InputLabel>Type</InputLabel>
                          <Select
                            value={field.type}
                            label="Type"
                            onChange={(e) => updateField(index, 'type', e.target.value)}
                            disabled={isEdit && modelData.definition?.isPublished}
                          >
                            <MenuItem value="string">String</MenuItem>
                            <MenuItem value="number">Number</MenuItem>
                            <MenuItem value="boolean">Boolean</MenuItem>
                            <MenuItem value="date">Date</MenuItem>
                            <MenuItem value="text">Text</MenuItem>
                          </Select>
                        </FormControl>
                      </Grid>
                      <Grid item xs={12} sm={2}>
                        <TextField
                          fullWidth
                          label="Default Value"
                          value={field.default || ''}
                          onChange={(e) => updateField(index, 'default', e.target.value)}
                          disabled={isEdit && modelData.definition?.isPublished}
                        />
                      </Grid>
                      <Grid item xs={12} sm={3}>
                        <Box display="flex" gap={1}>
                          <FormControlLabel
                            control={
                              <Checkbox
                                checked={field.required}
                                onChange={(e) => updateField(index, 'required', e.target.checked)}
                                disabled={isEdit && modelData.definition?.isPublished}
                              />
                            }
                            label="Required"
                          />
                          <FormControlLabel
                            control={
                              <Checkbox
                                checked={field.unique}
                                onChange={(e) => updateField(index, 'unique', e.target.checked)}
                                disabled={isEdit && modelData.definition?.isPublished}
                              />
                            }
                            label="Unique"
                          />
                        </Box>
                      </Grid>
                      <Grid item xs={12} sm={2}>
                        <IconButton
                          onClick={() => removeField(index)}
                          color="error"
                          disabled={isEdit && modelData.definition?.isPublished}
                        >
                          <DeleteIcon />
                        </IconButton>
                      </Grid>
                    </Grid>
                  </CardContent>
                </Card>
              ))}

              {modelData.definition.fields.length === 0 && (
                <Box textAlign="center" py={4}>
                  <Typography color="text.secondary">
                    No fields defined. Click "Add Field" to get started.
                  </Typography>
                </Box>
              )}
            </Grid>

            <Divider sx={{ my: 2 }} />

            {/* Ownership */}
            <Grid item xs={12}>
              <Typography variant="h6" gutterBottom>
                Ownership (Optional)
              </Typography>
              <TextField
                fullWidth
                label="Owner Field Name"
                value={modelData.definition.ownerField}
                onChange={(e) => handleInputChange('definition.ownerField', e.target.value)}
                placeholder="e.g., ownerId, userId"
                helperText="Field name that will store the owner's user ID"
                disabled={isEdit && modelData.definition?.isPublished}
              />
            </Grid>

            <Divider sx={{ my: 2 }} />

            {/* RBAC */}
            <Grid item xs={12}>
              <Typography variant="h6" gutterBottom>
                Role-Based Access Control
              </Typography>
              <Grid container spacing={2}>
                {['Admin', 'Manager', 'Viewer'].map((role) => (
                  <Grid item xs={12} sm={4} key={role}>
                    <Card>
                      <CardContent>
                        <Typography variant="subtitle1" gutterBottom>
                          {role}
                        </Typography>
                        <FormControl fullWidth>
                          <InputLabel>Permissions</InputLabel>
                          <Select
                            multiple
                            value={modelData.definition.rbac[role] || []}
                            onChange={(e) => updateRbac(role, e.target.value)}
                            renderValue={(selected) => (
                              <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
                                {selected.map((value) => (
                                  <Chip key={value} label={value} size="small" />
                                ))}
                              </Box>
                            )}
                            disabled={isEdit && modelData.definition?.isPublished}
                          >
                            <MenuItem value="create">Create</MenuItem>
                            <MenuItem value="read">Read</MenuItem>
                            <MenuItem value="update">Update</MenuItem>
                            <MenuItem value="delete">Delete</MenuItem>
                            <MenuItem value="all">All</MenuItem>
                          </Select>
                        </FormControl>
                      </CardContent>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            </Grid>

            {/* Actions */}
            <Grid item xs={12}>
              <Box display="flex" gap={2} justifyContent="flex-end">
                <Button
                  variant="outlined"
                  onClick={() => navigate('/models')}
                  disabled={saving}
                >
                  Cancel
                </Button>
                <Button
                  type="submit"
                  variant="contained"
                  startIcon={<SaveIcon />}
                  disabled={saving || modelData.definition.fields.length === 0}
                >
                  {saving ? 'Saving...' : isEdit ? 'Update Model' : 'Create Model'}
                </Button>
              </Box>
            </Grid>
          </Grid>
        </Box>
      </Paper>
    </Container>
  );
};

export default ModelEditor;
